    public function getAnswer(bool $partTwo): string
    {
        $input = $this->getPuzzleInput();

        $map = array_map('str_split', explode("\n", $input));

        $y = 0;
        $x = array_search('|', $map[0]);
        $direction = 0;
        $seen = '';
        $steps = 0;

        while(true) {
            //$this->output->writeln("$x,$y {$map[$y][$x]}");
            switch ($direction) {
                case 0:
                    $y++;
                    break;
                case 1:
                    $x++;
                    break;
                case 2:
                    $y--;
                    break;
                case 3:
                    $x--;
                    break;
            }
            $steps++;

            if (!isset($map[$y][$x])) {
                throw new \Exception("Not set. Fucked up!");
            }

            if ($map[$y][$x] == ' ') {
                break;
            }

            if (preg_match('/[A-Za-z]/', $map[$y][$x])) {
                $seen .= $map[$y][$x];
            }
            elseif (!in_array($map[$y][$x], ['|', '-'])) {
                // Now work out the next direction
                switch (true) {
                    case ($direction != 2 && ($map[$y + 1][$x] ?? '-') == '|'):
                        $direction = 0;
                        break;
                    case ($direction != 3 && ($map[$y][$x + 1] ?? '|') == '-'):
                        $direction = 1;
                        break;
                    case ($direction != 0 && ($map[$y - 1][$x] ?? '-') == '|'):
                        $direction = 2;
                        break;
                    case ($direction != 1 && ($map[$y][$x - 1] ?? '|') == '-'):
                        $direction = 3;
                        break;
                }
            }
        }

        return $partTwo ? $steps : $seen;
    }
