    @Override
    public String GetAnswer() {
        for(char character: this.input.toCharArray())
        {
            this.changeFloor(character);
        }

        return "" + this.location;
    }

    protected void changeFloor(char character) {
        switch (character)
        {
            case ')':
                this.location -= 1;
                break;
            case '(':
                this.location += 1;
                break;
            default:
                throw new IllegalArgumentException("Unknown character " + character);
        }
    }
